import React from "react";
//import { withRouter } from "react-router-dom";
import $ from "jquery"; 

import "../navigation/navigation.css";

class CommunicatorNavigation extends React.Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() { 
		this.cbpHorizontalMenu().init();
  }


  cbpHorizontalMenu() {
    var listItems = $("#navigation > ul > li");
    var menuItems = listItems.children("a");
    var body = $("body");
    var current = -1;

    function init() {
      listItems.each(function(){
        if (this.className === "submenu") {

          console.log('submenu');

          menuItems.on("click", open);
          listItems.on("click", function(event) { 
            event.stopPropagation(); 
          });
        }
      });
    }

    function open(event) {
      if( current !== -1 ) {
        listItems.eq(current).removeClass("cbp-hropen");
      }
  
      var item = $(event.currentTarget).parent("li"),
        idx = item.index();
  
      if( current === idx ) {
        item.removeClass("cbp-hropen");
        current = -1;
      }
      else {
        item.addClass("cbp-hropen");
        current = idx;
        //body.off("click").on("click", close);
      }
  
      return false;
  
    }

    function close(event) {
      listItems.eq(current).removeClass("cbp-hropen");
      current = -1;
    }


    return {init : init};
  }


  render() 
  {
    return (
      //cbp-hropen
      //border-radius: 0px; opacity: 0.9;
      <nav id="navigation" className="navbar navbar-inverse menu-background sht-navbar navbar-fixed-top cbp-hrmenu" style={{borderRadius: "0px", opacity: "1"}}>  
        <ul>
          <li>
            <a href="/default">Home</a>
          </li>
          <li className="submenu">
            <a href="#">Administration</a>
            <div className="cbp-hrsub">
              <div className="cbp-hrsub-inner"> 
                <div>
                  <h4>Custom Summary</h4>
                  <ul>
                    <li><a href="#">Chemical Area Administration</a></li>
                    <li><a href="#">SUmmary Administration</a></li>
                  </ul>
                </div>
                <div>
                  <h4>List/Databases</h4>
                  <ul>
                    <li><a href="#">Gadget Finder</a></li>
                    <li><a href="#">Green Tree Express</a></li>
                    <li><a href="#">Green Tree Pro</a></li>
                    <li><a href="#">Wobbler 3.0</a></li>
                    <li><a href="#">Coolkid</a></li>
                  </ul>
                </div>
                <div>
                  <h4>My Site</h4>
                  <ul>
                    <li><a href="#">Additional Properties</a></li>
                    <li><a href="#">Facility Identification</a></li>
                    <li><a href="#">Manage Users</a></li>
                    <li><a href="#">Preferences</a></li>
                    <li><a href="#">Print Barcodes</a></li>
                  </ul>
                </div>
                <div>
                </div>
              </div>
            </div>
          </li>
          <li>
            <a href="/chemical-area-ledger">Chemical Area Ledger</a>
          </li>
        </ul>
      </nav>
    )
  }


} 

export default CommunicatorNavigation;
