import React from "react";

export const AuthenicationService = {
    login,
    logout
};

const apiBaseUrl = 'http://apiuat.spheracloud.net';

async function login(e, username, password, corporateId) {
    const requestBody= {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userName: username, password: password, corporateId: corporateId })
    };

    return fetch(`${apiBaseUrl}/authorize`, requestBody)
