import React from "react";
import "../styles/login.css"; 

export default function Login() {

  function btnLoginButton_Click(e) {
    e.preventDefault();

    console.log('The login button was clicked.');

    login("training", "695Ni$$an", 1002);
  }

  function login(username, password, corporationid) {
    fetch("https://apiuat.spheracloud.net/api/v1/Authorize", {
      method: "POST",
      headers: new Headers({
        'Accept': 'application/json',
        'Content-Type': 'application/json', 
        'Access-Control-Allow-Origin': '*',
      }),
      body: JSON.stringify({
        userName: username,
        password: password,
        corporationId: corporationid
      })
    })
    .then((response) => { 
      console.log('Waiting for api response.');
      console.log(response.json());
    });
  }

  return (
    <div className="wrap loginFields">
      <div id="login">
        <div id="LoginHeader">Communicator Login</div>
        <div className="loginEntry">
          <input name="txtBxUserName" type="text" id="txtBxUserName" className="textEntry" placeholder="Login Name"></input>
          <input name="txtBxPassword" id="txtBxPassword" className="textEntry" type="password" placeholder="Password"></input>
          <input name="txtBxCompanyId" type="text" id="txtBxCompanyId" className="textEntry" placeholder="Company ID"></input>
        </div>
        <input type="submit" name="btnLoginButton" value="Log In" id="btnLoginButton" onClick={btnLoginButton_Click} />
      </div>
      <br/>
      <div id="ssl">
          <p>TLS Enabled</p>
      </div>
    </div>
  );
}
