import React from "react";

export const AuthenicationService = {
    login,
    logout
};

const apiBaseUrl = 'https://apiuat.spheracloud.net/';

async function login(username, password, corporateId) {
    const requestBody= {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userName: username, password: password, corporateId: corporateId })
    };

    return fetch(`${apiBaseUrl}/authenticate`, requestBody)
        .then(
            Response.then(text => {
                const data = JSON.parse(text);
                console.log(data);
            })
        )
        .then(token => {
            if (token) {
                localStorage.setItem('token', );
            }
            // // login successful if there's a user in the response
            // if (user) {
            //     // store user details and basic auth credentials in local storage 
            //     // to keep user logged in between page refreshes
            //     user.authdata = window.btoa(username + ':' + password);
            //     localStorage.setItem('user', JSON.stringify(user));
            // }

            return '';
        });
}

async function logout() {
    // remove user from local storage to log user out
    localStorage.removeItem('token');
}
