import React from "react";
import { TreeView, processTreeViewItems, handleTreeViewCheckChange, moveTreeViewItem, TreeViewDragAnalyzer, TreeViewDragClue } from '@progress/kendo-react-treeview'

import "../chemical-area-ledger/chemical-area-ledger.css";
import "../../styles/ledgers/ledgers.css";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css"

class ChemicalAreaLedger extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      chemicalAreas: [],
      chemicalAreasHierarchy: [],
    };
  }

  componentDidMount() {
    this.getChemicalAreas(233, 1, 100);
  }

  async getChemicalAreas(facilityId, pageIndex, pageSize) {
    await fetch(`/api/v1/Facility/${facilityId}/ChemicalAreas?pageIndex=${pageIndex}&pageSize=${pageSize}`, {
      method: 'GET',
      headers: {
        'Authorization': 'Bearer ' + localStorage.getItem('token'),
        'Content-Type': 'application/json'
      }
    })
    .then((response) => response.json())
    .then((data) => {
      var parents = data.filter(function (item) {
        return item.parentChemicalAreaId === 0;
      });
      var treeviewData = parents;

      var children = data.filter(function (item) {
        return item.parentChemicalAreaId > 0;
      });

      treeviewData.forEach(parent => {
        children.forEach(child => {
          if (child.parentChemicalAreaId === parent.id) {
            parent.items = child;
          }
        });
      });

      this.setState({
        chemicalAreas: data,
        chemicalAreaTreeView: treeviewData,
      })
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  }

  render() {

    function ChemicalAreaCards(props) {
      return (
        <div>
            {props.chemicalAreas.map((myChemicalArea, index) => (
              <div key={index} className="card">
                <div className="card-body">
                  <h5 className="card-title">{myChemicalArea.name}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">{myChemicalArea.description}</h6>
                  <p className="card-text">Use: {myChemicalArea.areaOfUse.areaOfUse}</p>
                </div>
              </div>
            ))}
        </div>
      )
    }


    function ChemicalAreaTreeView() {
      return (
        <TreeView
            data={this.state.chemicalAreasTreeViewData}
            textField="name"
            expandIcons={true}
        />
      )
    }
    
    return (
        <div className="content">
        <div id="chemicalAreaLedgerHeader" className="ledgerTitle">Chemical Area Ledger</div>
        <br/>
        <div>
        <ChemicalAreaTreeView/>
        <ChemicalAreaCards chemicalAreas={this.state.chemicalAreas} />
        </div>  
        </div>
    )
  }
} 

export default ChemicalAreaLedger;
