import React from "react";
import ReactDOM from 'react-dom';
import { Grid, GridColumn as Column } from '@progress/kendo-react-grid';

import "../user-management/user-management.css";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css"
import "../../styles/ledgers/ledgers.css";
import '../../../node_modules/@progress/kendo-theme-bootstrap/dist/all.css';

class UserManagement extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
        users: [],
        skip: 0,
        take: 25,
      };
  }

  componentDidMount() {
    this.getUsers(233, this.state.page, this.state.size);
  }

  pageChange = (event) => {
    this.setState({
        skip: event.page.skip,
        take: event.page.take
    });
  }

  async getUsers(facilityId, pageIndex, pageSize) {
    await fetch(`/api/v1/Facility/${facilityId}/Users`, {
      method: 'GET',
      headers: {
        'Authorization': 'Bearer ' + localStorage.getItem('token'),
        'Content-Type': 'application/json'
      }
    })
    .then((response) => response.json())
    .then((data) => {
      this.setState({
        users: data,
      });
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  }

  render() {

    function UserStatus(props) {
        const myUserStatus = props.status;
        if (myUserStatus) {
          return "Active";
        }
        return "Inactive";
      }

    return (
        <div className="content">
        <div id="chemicalAreaLedgerHeader" className="ledgerTitle">User Management</div>
        <br/>
        <div>
            <Grid
                style={{ height: '400px' }}
                data={this.state.users.slice(this.state.skip, this.state.take + this.state.skip)} 
                skip={this.state.skip}
                take={this.state.take}
                total={this.state.users.length}
                pageable={true}
                onPageChange={this.pageChange}
                >
                <Column field="lastName" title="Last Name" width="200px" />
                <Column field="firstName" title="First Name" width="200px" />
                <Column field="userName" title="User Name" />
                <Column field="userType.name" title="User Type" />
                <Column
                    field="status" title="Status"
                    cell={props => (
                        <td>
                            <UserStatus status={props.dataItem[props.field]}/>
                        </td>
                    )}
                />
            </Grid>
        </div>  
        </div>
    )
  }
} 

export default UserManagement;
