import React from "react";
//import { withRouter } from "react-router-dom";
import $ from "jquery"; 

import "../navigation/navigation.css";

class CommunicatorNavigation extends React.Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() { 
		this.cbpHorizontalMenu.init();
  }

  cbpHorizontalMenu = (function() {
    var $listItems = $("navigation > ul > li");
    var $menuItems = $listItems.children("a");
    var $body = $("body");
    var current = -1;
    
    console.log($listItems.length);
  
    function init() {
      $menuItems.on("click", open );
      $listItems.on("click", function( event ) { 
        event.stopPropagation(); 
      });
    }
  
    function open(event) {
  
      if( current !== -1 ) {
        $listItems.eq( current ).removeClass("cbp-hropen");
      }
  
      var $item = $(event.currentTarget ).parent("li"),
        idx = $item.index();
  
      if( current === idx ) {
        $item.removeClass("cbp-hropen");
        current = -1;
      }
      else {
        $item.addClass("cbp-hropen");
        current = idx;
        $body.off("click").on("click", close );
      }
  
      return false;
  
    }
  
    function close( event ) {
      $listItems.eq( current ).removeClass("cbp-hropen");
      current = -1;
    }
  
    return { init : init };
  })();

  render() 
  {
    return (
      //border-radius: 0px; opacity: 0.9;
      <nav id="navigation" className="navbar navbar-inverse menu-background sht-navbar navbar-fixed-top cbp-hrmenu" style={{borderRadius: "0px", opacity: "0.9"}}>
        
      
      
        <ul>
          <li className="">
            <a href="#">Products</a>
            <div className="cbp-hrsub">
              <div className="cbp-hrsub-inner"> 
                <div>
                  <h4>Learning And Games</h4>
                  <ul>
                    <li><a href="#">Catch the Bullet</a></li>
                    <li><a href="#">Snoopydoo</a></li>
                  </ul>
                </div>
                <div>
                  <h4>Utilities</h4>
                  <ul>
                    <li><a href="#">Gadget Finder</a></li>
                    <li><a href="#">Green Tree Express</a></li>
                    <li><a href="#">Green Tree Pro</a></li>
                    <li><a href="#">Wobbler 3.0</a></li>
                    <li><a href="#">Coolkid</a></li>
                  </ul>
                </div>
                <div>
                </div>
              </div>
            </div>
          </li>
          <li>...</li>
          <li>...</li>
        </ul>


      </nav>
    )
  }


} 

export default CommunicatorNavigation;
