import React from "react";
import "../chemical-area-ledger/chemical-area-ledger.css";
import "../../styles/ledgers/ledgers.css";

import { TreeView } from '@progress/kendo-react-treeview';

class ChemicalAreaLedger extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      chemicalAreas: [],
    };
  }

  componentDidMount() {
    this.getChemicalAreas(233, 1, 100);
  }

  async getChemicalAreas(facilityId, pageIndex, pageSize) {
    await fetch(`/api/v1/Facility/${facilityId}/ChemicalAreas?pageIndex=${pageIndex}&pageSize=${pageSize}`, {
      method: 'GET',
      headers: {
        'Authorization': 'Bearer ' + localStorage.getItem('token'),
        'Content-Type': 'application/json'
      }
    })
    .then((response) => response.json())
    .then((data) => {
      this.setState({
        chemicalAreas: data,
      })
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  }

  render() {

    const chemicalAreasList = this.state.chemicalAreas.map((myChemicalArea, index) => (
      <p key={index}>{myChemicalArea.name}</p>
    ))

    const chemicalAreaCards = ({ chemicalAreas }, index) => {
      return (
        <div>
          <center><h1>Chemical Areas</h1></center>
          {chemicalAreas.map((myChemicalArea) => (
            <div key={index} class="card">
              <div class="card-body">
                <h5 class="card-title">{myChemicalArea.name}</h5>
                <h6 class="card-subtitle mb-2 text-muted">{myChemicalArea.description}</h6>
                <p class="card-text">Area of use: {myChemicalArea.areaOfUse.areaOfUse}</p>
              </div>
            </div>
          ))}
        </div>
      )
    };

    return (
        <div className="content">
        <div id="chemicalAreaLedgerHeader" className="ledgerTitle">Chemical Area Ledger</div>
        <br/>
        <div>
          {chemicalAreaCards}
        </div>  
        </div>
    )
  }
} 

export default ChemicalAreaLedger;
