import React from "react";
import "../default-footer/default-footer.css";

class ChemicalAreaLedger extends React.Component {

  constructor(props) {
    super(props);
  }

  load() {
    this.getChemicalAreas(233, 1, 100);
  }

  async getChemicalAreas(facilityId, pageIndex, pageSize) {
    await fetch(`/api/v1/Facility/${facilityId}/ChemicalAreas?pageIndex=${pageIndex}&pageSize=${pageSize}`, {
      method: 'GET',
      headers: {
        'Authorization': 'Bearer ' + localStorage.getItem('token'),
        'Content-Type': 'application/json'
      }
    })
    .then((response) => response.json())
    .then((data) => {
      console.log(data);
      var myChemicalAreas = data;
      var myParentChemicalAreas = myChemicalAreas.filter(function (item) {
        return item.parentChemicalAreaId === 0;
      });
      var mychildrenChemicalAreas = myChemicalAreas.filter(function (item) {
        return item.parentChemicalAreaId !== 0;
      });
      
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  }

  render() {
    return (
        <div>
            {this.load()}
        </div>
    )
  }
} 

export default ChemicalAreaLedger;
