import React from "react";
import "../styles/login.css"; 
import { Redirect } from 'react-router-dom'


export default function Login(props) {

  function btnLoginButton_Click(e) {
    e.preventDefault();

    login("training", "695Ni$$an", 1002);

    if (localStorage.getItem('accessToken') != null)
    {
      props.history.push("/default");
    }
  }

  function login(myUsername, myPassword, myCorporationId) {
    const userLoginData = { username: myUsername, password: myPassword, corporationId: myCorporationId};

    fetch('/api/v1/Authorize', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userLoginData),
    })
    .then((response) => response.json())
    .then((data) => {
      localStorage.setItem('accessToken', data.accessToken);
    })
    .catch((error) => {
      console.error('Error:', error);
    });

  }

  return (
    <div className="wrap loginFields">
      <div id="login">
        <div id="LoginHeader">Communicator Login</div>
        <form>
          <div className="loginEntry">
            <input name="txtBxUserName" type="text" id="txtBxUserName" name="username" className="textEntry" placeholder="Login Name"></input>
            <input name="txtBxPassword" id="txtBxPassword" className="textEntry" type="password" placeholder="Password"></input>
            <input name="txtBxCompanyId" type="text" id="txtBxCompanyId" className="textEntry" placeholder="Company ID"></input>
          </div>
        </form>
        <input type="submit" name="btnLoginButton" value="Log In" id="btnLoginButton" onClick={btnLoginButton_Click} />
      </div>
      <br/>
      <div id="ssl">
          <p>TLS Enabled</p>
      </div>
    </div>
  );
}
