import React from "react";
import "../styles/login.css"; 
//import axios from 'axios';

export default function Login() {

  function btnLoginButton_Click(e) {
    e.preventDefault();

    console.log('The login button was clicked.');
    login("training", "695Ni$$an", 1002);
  }

  // async function apiCall(url = '', method='', cors='', myHeaders={}, data = {}) {
  //   // Default options are marked with *
  //   const response = await fetch(url, {
  //     method: method, // *GET, POST, PUT, DELETE, etc.
  //     mode: cors, // no-cors, *cors, same-origin
  //     cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
  //     credentials: 'same-origin', // include, *same-origin, omit
  //     headers: {
  //       'Content-Type': 'application/json'
  //       // 'Content-Type': 'application/x-www-form-urlencoded',
  //     },
  //     redirect: 'follow', // manual, *follow, error
  //     referrerPolicy: 'no-referrer', // no-referrer, *client
  //     body: JSON.stringify(data) // body data type must match "Content-Type" header
  //   });
  //   return await response.json(); // parses JSON response into native JavaScript objects
  // }

  function login(myUsername, myPassword, myCorporationId) {
    const userLoginData = { username: myUsername, password: myPassword, corporationId: myCorporationId};
    fetch('/api/v1/Authorize', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userLoginData),
    })
    .then((response) => response.json())
    .then((data) => {
      localStorage.setItem('accessToken', data.accessToken);
    })
    .catch((error) => {
      console.error('Error:', error);
    });

  }

  return (
    <div className="wrap loginFields">
      <div id="login">
        <div id="LoginHeader">Communicator Login</div>
        <div className="loginEntry">
          <input name="txtBxUserName" type="text" id="txtBxUserName" className="textEntry" placeholder="Login Name"></input>
          <input name="txtBxPassword" id="txtBxPassword" className="textEntry" type="password" placeholder="Password"></input>
          <input name="txtBxCompanyId" type="text" id="txtBxCompanyId" className="textEntry" placeholder="Company ID"></input>
        </div>
        <input type="submit" name="btnLoginButton" value="Log In" id="btnLoginButton" onClick={btnLoginButton_Click} />
      </div>
      <br/>
      <div id="ssl">
          <p>TLS Enabled</p>
      </div>
    </div>
  );
}
