import React from "react";
import "../chemical-area-ledger/chemical-area-ledger.css";

class ChemicalAreaLedger extends React.Component {

  constructor(props) {
    super(props);
  }

  load() {
    this.getChemicalAreas(233, 1, 100);
  }

  async getChemicalAreas(facilityId, pageIndex, pageSize) {
    await fetch(`/api/v1/Facility/${facilityId}/ChemicalAreas?pageIndex=${pageIndex}&pageSize=${pageSize}`, {
      method: 'GET',
      headers: {
        'Authorization': 'Bearer ' + localStorage.getItem('token'),
        'Content-Type': 'application/json'
      }
    })
    .then((response) => response.json())
    .then((data) => {
      var myParentChemicalAreas = data.filter(function (item) {
        return item.parentChemicalAreaId === 0;
      });
      var myChildChemicalAreas = data.filter(function (item) {
        return item.parentChemicalAreaId !== 0;
      });

      console.log(myParentChemicalAreas);
      console.log(myChildChemicalAreas);
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  }

  render() {
    return (
        <div className="content">
        <div id="chemicalAreaLedgerHeader" className="SiteHawkTitle">Chemical Area Ledger</div>
        <br/>
        <div>
          {this.load()}
        </div>
        </div>
    )
  }
} 

export default ChemicalAreaLedger;
