import React, { useState } from "react";
import { Button, Form, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import "../styles/Login.css";

const Login = (props) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [corporationId, setCorporationId] = useState("");

  function validateForm() {
    return username.length > 0 && password.length > 0 && corporationId > 0;
  }

  function handleSubmit(event) {
    event.preventDefault();
  }

  return (
    <div className="Login">
      <form onSubmit={handleSubmit}>
        <Form.FormGroup controlId="username" bsSize="large">
          <Form.ControlLabel>User Name:</Form.ControlLabel>
          <Form.FormControl
            autoFocus
            type="text"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
        </Form.FormGroup>
        <Form.FormGroup controlId="password" bsSize="large">
          <Form.ControlLabel>Password:</Form.ControlLabel>
          <FormControl
            value={password}
            onChange={e => setPassword(e.target.value)}
            type="password"
          />
        </Form.FormGroup>
        <Form.FormGroup controlId="corporationid" bsSize="large">
          <Form.ControlLabel>Corporartion Id:</Form.ControlLabel>
          <Form.FormControl
            value={corporationId}
            onChange={e => setCorporationId(e.target.value)}
            type="text"
          />
        </Form.FormGroup>
        <Button block bsSize="large" disabled={!validateForm()} type="submit">
          Login
        </Button>
      </form>
    </div>
  );
}


export default Login;
